	(191,'_site_transient_update_plugins','O:8:\"stdClass\":4:{s:12:\"last_checked\";i:1461546026;s:8:\"response\";a:0:{}s:12:\"translations\";a:0:{}s:9:\"no_update\";a:7:{s:23:\"debug-bar/debug-bar.php\";O:8:\"stdClass\":7:{s:2:\"id\";s:5:\"18561\";s:4:\"slug\";s:9:\"debug-bar\";s:6:\"plugin\";s:23:\"debug-bar/debug-bar.php\";s:11:\"new_version\";s:5:\"0.8.4\";s:3:\"url\";s:40:\"https://wordpress.org/plugins/debug-bar/\";s:7:\"package\";s:58:\"https://downloads.wordpress.org/plugin/debug-bar.0.8.4.zip\";s:14:\"upgrade_notice\";s:56:\"Updated to avoid incompatibilities with some extensions.\";}s:23:\"developer/developer.php\";O:8:\"stdClass\":6:{s:2:\"id\";s:5:\"24908\";s:4:\"slug\";s:9:\"developer\";s:6:\"plugin\";s:23:\"developer/developer.php\";s:11:\"new_version\";s:5:\"1.2.6\";s:3:\"url\";s:40:\"https://wordpress.org/plugins/developer/\";s:7:\"package\";s:58:\"https://downloads.wordpress.org/plugin/developer.1.2.6.zip\";}s:33:\"monster-widget/monster-widget.php\";O:8:\"stdClass\":6:{s:2:\"id\";s:5:\"31537\";s:4:\"slug\";s:14:\"monster-widget\";s:6:\"plugin\";s:33:\"monster-widget/monster-widget.php\";s:11:\"new_version\";s:3:\"0.3\";s:3:\"url\";s:45:\"https://wordpress.org/plugins/monster-widget/\";s:7:\"package\";s:57:\"https://downloads.wordpress.org/plugin/monster-widget.zip\";}s:47:\"regenerate-thumbnails/regenerate-thumbnails.php\";O:8:\"stdClass\":6:{s:2:\"id\";s:4:\"4437\";s:4:\"slug\";s:21:\"regenerate-thumbnails\";s:6:\"plugin\";s:47:\"regenerate-thumbnails/regenerate-thumbnails.php\";s:11:\"new_version\";s:5:\"2.2.6\";s:3:\"url\";s:52:\"https://wordpress.org/plugins/regenerate-thumbnails/\";s:7:\"package\";s:64:\"https://downloads.wordpress.org/plugin/regenerate-thumbnails.zip\";}s:47:\"show-current-template/show-current-template.php\";O:8:\"stdClass\":6:{s:2:\"id\";s:5:\"41022\";s:4:\"slug\";s:21:\"show-current-template\";s:6:\"plugin\";s:47:\"show-current-template/show-current-template.php\";s:11:\"new_version\";s:5:\"0.2.2\";s:3:\"url\";s:52:\"https:/